package com.upskill.java_1;

public class AccessModifier {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		
    public String Name ="Madan";  // public - Any class can access
	private	    int age =25;             // private -only -same class can access
	protected 	int ssn=12345;         //protected -classes in the same package and subclass
		        String address ="Heritage Tower";  // default -classes in same package can access
		        
	}

}
